YUI().use("event","node","history-hash",function(Y){Y.on("domready",function(){if(Y.one(".tab-menu")!==null){var hash=window.location.hash;Y.all(".tab-content .tab:first-child").removeClass("hide");Y.all(".tab-menu li:first-child").addClass("actif");Y.all(".tab-menu li").on("click",function(e){setTab(e)});Y.one("window").on("hashchange",function(e){setTab()});var setTab=function(e){hash=window.location.hash;var tabsNode;if(hash!==""&&hash!==undefined){if(Y.one(hash)==undefined){return}tabsNode=Y.one(hash).ancestor(".tab-content");var menuNode=Y.one("#menu_"+hash.substr(1));tabsNode.all(".tab").addClass("hide");Y.one(hash).removeClass("hide");Y.all(".tab-menu li").removeClass("actif");menuNode.addClass("actif")}else if(e){var node=e.currentTarget;var id=node.getData("target");tabsNode=node.ancestor().ancestor();tabsNode.all(".tab-content .tab").addClass("hide");Y.one("#"+id).removeClass("hide");tabsNode.all(".tab-menu li").removeClass("actif");node.addClass("actif");e.preventDefault()}};setTab()}})});